package com.dpgil.pathlinker.path_linker.internal.util;

import java.util.ArrayList;

import io.swagger.annotations.ApiModel;
import io.swagger.annotations.ApiModelProperty;

/**
 * Path class that represents the path generated by PathLinker
 *      Each path contains a path index in the corresponding network,
 *      the total path weight
 *      an array list of nodes names that is included in the path
 *      
 * @author Li Huang
 * @version 12.7.2017
 */
@ApiModel(value="PathLinker Path", 
description="Path object consists of path index, path score, and the name of vertices that constructs the path")
public class PathLinkerPath {

    /** the path index */
    private int pathIndex;
    /** the total score of the path */
    private double  pathScore;
    /** the list of nodes in string in order in the path */
    private ArrayList<String> path;

    /**
     * Default constructor
     * @param pathIndex     the path index
     * @param pathScore    the path score
     * @param path      the path list
     */
    public PathLinkerPath (int pathIndex, double pathScore, ArrayList<String> path) {
        this.pathIndex = pathIndex;
        this.pathScore = pathScore;
        this.path = path;
    }

    /**
     * Getter method for path index
     * @return the pathIndex
     */
    @ApiModelProperty(value = "The total score/lengths the path consists.")
    public int getPathIndex() {
        return pathIndex;
    }

    /**
     * Getter method for path score
     * @return the pathScore
     */
    @ApiModelProperty(value = "The index of the path in the given list of PathLinkerPath. The index is indicated by path score.")
    public double getPathScore() {
        return pathScore;
    }

    /**
     * Getter method for the path list
     * @return the path
     */
    @ApiModelProperty(value = "The name of the vertices that constructs the path, The name matchs to the name column in the Node Table.")
    public ArrayList<String> getPath() {
        return path;
    }
}
